import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import './components/Pokemon/Pokemon';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {name: "",weight: 0, abilities: [],image:""};
  }
  getPokemon() {
    return fetch('https://pokeapi.co/api/v2/pokemon/'+this.props.idPokemon+"/")
    .then((response) => response.json())
    .then((responseJson) => {
      this.setState({name: responseJson.name,
      weight :responseJson.weight,abilities :responseJson['abilities'][0].ability.name,
        image : responseJson.sprites.front_shiny

      })    })
    .catch((error) => {
      console.error(error);
    });
  }

  componentDidMount() {
    this.getPokemon();
  }

  render() {
    return (
      <div>
        <Pokemon idPokemon={this.props.idPokemon}/> 

      </div>

    )
  }
}


export default App;
