import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {name: ""};
  }
  getPokemon() {
    return fetch('https://pokeapi.co/api/v2/pokemon/bulbasaur/')
    .then((response) => response.json())
    .then((responseJson) => {
      this.setState({name: responseJson.weight
      })    })
    .catch((error) => {
      console.error(error);
    });
  }

  componentDidMount() {
    this.getPokemon();
  }

  render() {
    return (
      <div>
        <h1>Hello, {this.state.name}</h1>
      </div>

    )
  }
}


export default App;
